const _map = new Map(MENU_INDEX.map(e => [e.key, e]));

export function get(key) {
  if (!key) return undefined;
  return _map.get(String(key).toUpperCase());
}

export function tName(item, prefLangs = ['ru','pl','en','ua','vn','de','zh']) {
  if (!item) return '';
  const n = item.name || {}
  for (const l of prefLangs) if (n[l]) return n[l];
  // fallback: any value or key
  const any = Object.values(n)[0];
  return any || item.key;
}

export function tDesc(item, prefLangs = ['ru','pl','en','ua','vn','de','zh']) {
  if (!item) return '';
  const d = item.description || {}
  for (const l of prefLangs) if (d[l]) return d[l];
  return '';
}

export function imageUrl(item, prefix = 'img/') {
  if (!item || !item.image) return '';
  return prefix + item.image;
}

export function byCategory(category) {
  if (!category) return [];
  const c = String(category).toLowerCase();
  return MENU_INDEX.filter(e => (e.category || '').toLowerCase() === c);
}

export function search(query, opts = {}) {
  const q = String(query || '').trim().toLowerCase();
  const lang = opts.lang || null;
  const category = opts.category ? String(opts.category).toLowerCase() : null;
  const tags = Array.isArray(opts.tags) ? opts.tags.map(t => String(t).toLowerCase()) : null;
  return MENU_INDEX.filter(e => {
    if (category && String(e.category||'').toLowerCase() !== category) return false;
    if (tags && tags.length) {
      const et = (e.tags || []).map(t => String(t).toLowerCase());
      for (const t of tags) if (!et.includes(t)) return false;
    }
    if (!q) return true;
    const hay = [
      e.key,
      ...(Object.values(e.name||{})),
      ...(Object.values(e.description||{})),
      e.category || '',
      ...(e.tags || [])
    ]
    .join(' ')
    .toLowerCase();
    return hay.includes(q);
  });
}

